import { useRouter } from 'next/router'

export default function Clip() {
  const router = useRouter()
  let clipId = router.query.id

  async function getAudioClip () {
    let reqClip = await fetch(`https://api.audioboom.com/audio_clips/${clipId}.mp3`)
    let dataClip = await reqClip.json()
    let audioClip = dataClip.body.audio_clip
    console.log(audioClip)
  }

  getAudioClip()


  return (<h1>Podcast</h1>)
}
