import { useRouter } from 'next/router'

export default function Clip() {
    const router = useRouter()
    var clipId = router.query.id
    var clip = {}

    async function getAudioClip() {
      var reqClip = await fetch(`https://api.audioboom.com/audio_clips/${clipId}.mp3`)
      var clipJson = await reqClip.json()
      clip = clipJson.body.audio_clip
      console.log(clip.title)
    }

    getAudioClip()

  return(
    <div>
      <h1>{clip.title}</h1>
    </div>
  )
}
