import { useRouter } from 'next/router'

export default function Clip() {
    const router = useRouter()
    var clipId = router.query.id

    async function getAudioClip() {
      var reqClip = await fetch(`https://api.audioboom.com/audio_clips/${clipId}.mp3`)
      var clip = await reqClip.json()
      return clip
    }

    getAudioClip()
    console.log(clip)

  return(
    <div>
      {/* <h1>{audioClip.title}</h1> */}
    </div>
  )
}
