import Link from 'next/link'

export default class PodcastView extends React.Component {
  render() {
    const { clip } = this.props

    return (
      <div clasName="modal">
        <div className="clip" >
          <nav>
            <Link href={`/channel?id=${clip.channel.id}`} >
              <a className="close" >Volver</a>
            </Link>
          </nav>
          <picture>
            <div style={{backgroundImage:`url(${clip.urls.image})`}} />
          </picture>

          <div className="player">
            <h3>{clip.title}</h3>
            <h6>{clip.channel.title}</h6>
            <audio autoPlay="autoplay" controls="controls">
              <src src={clip.urls.high_mp3} type="audio/mpeg" />
            </audio>
          </div>
        </div>
        <style jsx>{`
        nav {
          background: none;
        }
        nav a {
          display: inline-block;
          padding: 15px;
          color: white;
          cursor: pointer;
          text-decoration: none;
        }
        .clip {
          display: flex;
          height: 100%;
          flex-direction: column;
          background: #8756ca;
          color: white;
        }
        picture {
          display: flex;
          align-items: center;
          justify-content: center;
          flex: 1 1;
          flex-direction: column;
          width: auto;
          padding: 10%;
        }
        picture div {
          width: 100%;
          height: 100%;
          background-position: 50% 50%;
          background-size: contain;
          background-repeat: no-repeat;
          padding-top: 15%;
        }
        .player {
          padding: 30px;
          background: rgba(0,0,0,0.3);
          text-align: center;
        }
        h3 {
          margin: 0;
        }
        h6 {
          margin: 0;
          margin-top: 1em;
        }
        audio {
          margin-top: 2em;
          width: 100%;
        }

        .modal {
          position: fixed;
          top: 0;
          left: 0;
          right: 0;
          bottom: 0;
          z-index: 99999;
        }
      `}</style>
      </div>
    )
  }
}
