export default class extends React.Component {

  static async getInitialProps ({ query }) {
    let idChannel = query.id
    let reqChannel = await fetch(`https://api.audioboom.com/channels/${ idChannel  }`)
    let dataChannel = await reqChannel.json()
    let channel = dataChannel.body.channel

    let reqAudio = await fetch(`https://api.audioboom.com/channels/${ idChannel  }/audio_clips`)
    let dataAudios = await reqAudio.json()
    let audioClips = dataAudios.body.audio_clips

    return {channel, audioClips}
  }

  render() {
    const {channel} = this.props
    return <div>
      <header>Podcast</header>
      <h1>{channel.title}</h1>
      <style jsx>{`
          header {
            color: #fff;
            background: #8756ca;
            padding: 15px;
            text-align: center;
          }
          .channels {
            display: grid;
            grid-gap: 30px;
            padding: 20px;
            grid-template-columns: repeat(auto-fill, minmax(300px, 1fr));
          }
          .channel {
            display: block;
            border-radius: 3px;
            box-shadow: 0px 2px 6px rgba(0, 0, 0, 0.15);
            margin-bottom: 0.5;
          }
          .channel img {
            width: 100%;
          }
          h2 {
            padding: 5px;
            font-size: 0.9em;
            font-weight: 600;
            margin: 0;
            text-align: center;
          }
        `}</style>
        <style jsx global>{`
          body {
            margin: 0;
            font-family: system-ui;
            background: white;
          }  
        `}</style>
    </div>
  }
}
